SELECT A.CD_ATENDIMENTO ATENDIMENTO, TO_CHAR(A.DT_ATENDIMENTO,'DD/MM/YYYY') DATA, TO_CHAR(A.HR_ATENDIMENTO,'HH24:MI') HORA, TIP.DS_TIP_MAR, P.NM_PRESTADOR FROM ATENDIME A, TIP_MAR TIP, PRESTADOR P WHERE A.CD_TIP_MAR = TIP.CD_TIP_MAR(+) AND A.CD_PRESTADOR = P.CD_PRESTADOR AND  A.CD_PACIENTE = 1272011